let initialState = {
  candidates: [
    {name: 'Jacob', voteCount: 0},
    {name: 'Adrian', voteCount: 0},
    {name: 'Hexx', voteCount: 0}
  ],
  totalVotes: 0
}

function candidateReducer(state = initialState, action) {
  let {type, payload} = action;

  switch(type) {
    case 'ADD_VOTE':

      let totalVotes = state.totalVotes + 1;
      let newCandidates = state.candidates.map(person => {
        if (person.name === payload) {
          return {...person, voteCount: person.voteCount + 1}
        } else {return person}
      })
      return{candidates: newCandidates, totalVotes}
    case 'RESET_VOTES':
      return initialState
    default:
      return state
  }
}

export default candidateReducer;
